mapper.select:
SELECT c.id, c.sender_id AS senderId, c.user_id AS userId, d.user_name AS senderName, c.user_name AS cuserName, c.order_num AS orderNum, c.order_msg AS orderMsg, c.commission, c.STATUS, c.create_time AS createTime, c.update_time AS updateTime FROM ( SELECT a.*, b.user_name FROM `order` a LEFT JOIN `user` b ON a.user_id = b.id WHERE a.sender_id = #{senderId} ) c, `user` d WHERE c.sender_id = d.id
and d.user_name=#{senderName}
and c.user_name=#{name}
and c.STATUS=#{status}
order by c.create_time desc

SELECT t1.id, t1.sender_id AS senderId, t1.user_id AS userId, t2.user_name AS senderName, t1.order_num AS orderNum, t1.order_msg AS orderMsg, t1.commission, t1.`status`, t1.create_time AS createTime, t1.update_time AS updateTime, t3.user_name as cuserName FROM `order` t1 INNER JOIN user t2 on t1.sender_id = t2.id LEFT JOIN user t3 on t1.user_id = t3.id WHERE (t1.user_id=#{userId} or t1.status=1)
and t2.user_name=#{senderName}
and t3.user_name=#{name}
and t1.status=#{status}
order by t1.create_time desc
